import React ,{useState}from "react";


function AddTransactionForm() {


   const[formData, setFormData] = useState ({
    date: "",
    description: "",
    category: "",
    amount: 0

   })

function handleSubmit(e){
  e.preventDefault();
fetch("  http://localhost:8001/transactions" ,{
  method: "POST",
  headers: {
    "Content-Type": "application/json",
  },
  body: JSON.stringify(formData),

});


}




   function handle(event){
    setFormData({
      ...formData,
      [event.target.id]: event.target.value,
    })
   }

  return (
    <div className="ui segment">
      <form onSubmit ={(e) => handleSubmit(e)} className="ui form">
        <div className="inline fields">
          <input onChange={(e) =>handle(e)} id="date" value={formData.date}type="date" name="date" />
          <input  onChange={(e) =>handle(e)} id="description" value={formData.description} type="text" name="description" placeholder="Description" />
          <input  onChange={(e) =>handle(e)} id="category" value={formData.category} type="text" name="category" placeholder="Category" />
          <input  onChange={(e) =>handle(e)} id="amount" value={formData.amount} type="number" name="amount" placeholder="Amount" step="0.01" />
        </div>
        <button className="ui button" type="submit">  
          Add Transaction
        </button>
      </form>


    </div>
  );
}

export default AddTransactionForm;
